import '../RelatedCourses/RelatedCourses.css'
import { useState, useEffect, Fragment } from 'react';
import axios from 'axios';
import { ProfileCard } from '../../Components'


export const RelatedCourses = ({singleProfile, onQueryParametersChange}) => {

    const [queryParameters, setQueryParameters] = useState({ Class: [], Subject:[] });

    const {_id, Course} = singleProfile
    console.log(Course)

    useEffect(() => {
        if (Course) {
            const classes = [];
            const subjects = [];
            Course.forEach(element => {
                if (!classes.includes(element.Class)) {
                    classes.push(element.Class);
                }
                if (!subjects.includes(element.Subject)) {
                    subjects.push(element.Subject);
                }
            });

            setQueryParameters({ Class: classes, Subject: subjects });
        }
    }, [Course]);

    useEffect(() => {
        onQueryParametersChange((prev) => {
            const updatedParameters = { ...prev, queryParameters };
            return updatedParameters;
        });
    }, [Course]);



    const params = new URLSearchParams();
    for (const key in queryParameters) {
        if (Array.isArray(queryParameters[key])) {
            queryParameters[key].forEach(value => params.append(key, value));
        } else {
            console.log(key, queryParameters[key]);
            params.append(key, queryParameters[key]);
        }
    }

    const [profiles, setProfiles] = useState([]);
    useEffect(() => {
        (async () => {
            try {
                const {data} = await axios.get(`http://localhost:3500/api/filter`, { params });
                setProfiles(data)
                console.log(profiles)
            } catch (error) {
                console.log(error);
            }
        })();
    }, [queryParameters])


    return (
        <Fragment>
            <h3>RelatedCourses</h3>
            <main className='main'>
            {
                profiles && profiles
                .filter(profile => profile._id !== _id)
                .map((profile) => <ProfileCard key={profile._id} profile={profile}/>)
            }
            </main>
        </Fragment>
    ) 
}
