import { Fragment, useEffect, useState } from "react"
import '../SignInSignUpPage/SignInSignUpPage.css';
import { useNavigate } from 'react-router-dom';
import axios from "axios";

export const SignInSignUpPage = () => {
    const [activeTab, setActiveTab] = useState('Login');

    const renderContent = () => {
        if (activeTab === 'Login') {
            return <Login />;
        } else if (activeTab === 'SignUp') {
            return <SignUp setActiveTab={setActiveTab}/>;
        }
    };

    return (<Fragment>
        <div className="heading-1">
        <a className="link " href="/">Shikshana</a>
        </div>

        <div className="login-container">

            <div className="tabs">
                <button
                    className={`tab ${activeTab === 'Login' ? 'active' : ''}`}
                    onClick={() => setActiveTab('Login')}
                >
                    Login
                </button>
                <button
                    className={`tab ${activeTab === 'SignUp' ? 'active' : ''}`}
                    onClick={() => setActiveTab('SignUp')}
                >
                   SignUp
                </button>
            </div>
            <div className="content">
                {renderContent()}
            </div>
        </div>
    </Fragment>)
}

const Login = () => {

    const [userDetails, setUserDetails] = useState({
        email: '',
        password: ''
    })

    const [error, setError] = useState('');

    const handleChange = (event) => {
        setUserDetails({
            ...userDetails,
            [event.target.name]: event.target.value
        })
    }
    const navigate = useNavigate();


    const handleSubmit = async (e) => {
        e.preventDefault()
        console.log(userDetails)
        try {
            const response = await axios.post(`http://localhost:3500/api/auth/login`,  {
                    email: userDetails.email,
                    password: userDetails.password
                }
            )
            console.log(response.data)
            navigate('/Home');

        } catch (error) {
            console.error(error.response.data.message);
            setError(error.response.data.message)
        }

    }

    return (
        <div>
            <h2>Login Page</h2>
            <br/>
            <form className="login-wrapper" onSubmit={handleSubmit}>
            <label>
                    Login as:
                    <select name="userType">
                    <option value="" disabled>Select user type</option>
                        <option value="student">Student</option>
                        <option value="faculty">Faculty</option>
                    </select>
                </label>
                <br/>
                <label>
                    Email:
                    <input type="email" name="email" value={userDetails.email} onChange={handleChange} required/>
                </label>
                <br/>
                <label>
                    Password:
                    <input type="password" name="password" value={userDetails.password} onChange={handleChange} required/>
                </label>
                <br/>
                {error && <p style={{color: 'red'}}>{error}</p>}
                <button type="submit">Login</button>
            </form>
        </div>
    )
}

const SignUp = ({setActiveTab}) => {
    const [formData, setFormData] = useState({
        loginAs: '',
        userName: '',
        email: '',
        phoneNumber: '',
        password: '',
        reenterPassword: '',
    });
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (formData.password != formData.reenterPassword){
            setError('Passwords do not match')
        }else{
            setError('');
            console.log('poo',formData.loginAs)
            if(formData.loginAs == 'student'){
                try {
                    const response = await axios.post('http://localhost:3500/api/auth/register', formData);
                    console.log(response);
                    setActiveTab('Login');
                } catch (error) {
                    console.log(error);
                }
            }
            else{
                try {
                    const response = await axios.post('http://localhost:3500/api/profiles/addProfile', {
                        "loginAs" : formData.loginAs,
                        "password": formData.password,
                        "Name": formData.userName,
                        "ContactNumber": formData.phoneNumber,
                        "ContactMail": formData.email
                    })
                    console.log(response);
                    setActiveTab('Login');
                } catch (error) {
                    console.log(error)
                }
            }


        }
    }

    const handleChange = (event) => {
        setFormData({
            ...formData,
            [event.target.name]: event.target.value
        })
    }


    return (

        <div>
            <h2> SignUp</h2>

            <form className="login-wrapper" onSubmit={handleSubmit}>
            <label>
                    SignUp as:
                    <select name="loginAs" value={formData.loginAs} onChange={handleChange} required>
                        <option value="" disabled>Select user type</option>
                        <option value="student">Student</option>
                        <option value="faculty">Faculty</option>
                    </select>
            </label>
            <br/>
            <label>
                Username:
                <input type="text" name="userName" value={formData.userName} onChange={handleChange} required/>
            </label>
            <br/>
            <label>
                Email:
                <input type="email" name="email" value={formData.email} onChange={handleChange} required/>
            </label>
            <br/>
            <label>
                Phone Number:
                <input type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange}/>
                <span style={{ color: 'darkgray', fontSize: 'small' }}>(optional)</span>
            </label>
            <br/>
            <label>
                Password:
                <input type="password" name="password" value={formData.password} onChange={handleChange} required/>
            </label>
            <br/>
            <label>
                Re-enter Password:
                <input type="password" name="reenterPassword" value={formData.reenterPassword} onChange={handleChange} required/>
            </label>
            <br />
            {error && <p style={{color: 'red'}}>{error}</p>}
            <button type="submit">SignUp</button>
            </form>
        </div>
    )
}
