import { Fragment, useState } from "react"
import "./filterbox.css";
import { SingleFilter } from "../singleFilter/singleFilter";


export const FilterBox = ({ onQueryParametersChange }) => {
    const [categoryOptions, setCategoryOptions] = useState([]);
    const [cityOptions, setCityOptions] = useState([]);

    const dataCategory = [{Category: "online"}, {Category: "home_tutions"},{Category: "in_person"}, {Category: "home_tutions1"}, {Category: "home_tutions2"},{Category: "home_tutions3"}, {Category: "home_tutions4"}]                
    const dataCity = [{City: "Bangalore"}, {City: "Manglore"}, {City: "Vishakapatnam"}, {City: "Hyderabad"}];

    const handleFilterChange = (filter, options) => {
        if (filter === 'Category') {
            setCategoryOptions(options);
        } else if (filter === 'City') {
            setCityOptions(options);
        }
    };

    useEffect(() => {
        onQueryParametersChange({ Category: categoryOptions, City: cityOptions });
    }, [categoryOptions, cityOptions, onQueryParametersChange]);

    return (
       <Fragment>
            <h1>Filter</h1>
            <div className="Singlefilter">
            <SingleFilter data={dataCategory} filter={"Category"} onFilterChange={handleFilterChange} />
            <SingleFilter data={dataCity} filter={"City"} onFilterChange={handleFilterChange}/>
            </div>

       </Fragment>

    )
}
