import { Fragment, useEffect, useState } from "react"
import "./singleFilter.css"
import axios from "axios";

export const SingleFilter = (props) => {

    const [filterOptions, setfilterOptions] = useState([]);
    const [isFiltersVisible, setIsFiltersVisible] = useState(true);
    const [selectedCategories, setSelectedCategories] = useState([]);

    var data;
    useEffect(()=> {
        (async () => {
            try {
                data = props.data
                setfilterOptions(data)
            } catch (error) {
                console.log(error)
            }
        })()
    }, [])



    const handleOnClick = async () => {
        setIsFiltersVisible((prev) => !prev);
    }

    const getFilterOptions = (option) => {
        setSelectedCategories(prevOptions => {
            if (prevOptions.includes(option)) {
                return prevOptions.filter(o => o !== option);
            } else {
                return [...prevOptions, option];
            }
            props.onFilterChange(props.filter, props.updatedOptions);
        }); 
    };

    useEffect(() => {
        const queryParameters = {
            [props.filter]: selectedCategories,
        };
        props.onQueryParametersChange(queryParameters);
    }, [selectedCategories]);



    return (
       <Fragment>
        <button onClick={handleOnClick}>
                <span className="material-icons-outlined" >
                {isFiltersVisible ? "keyboard_arrow_up" : "keyboard_arrow_down"}
                </span>
        </button>
        <h3>{props.filter}</h3>

            <div id="filters" style={{ display: isFiltersVisible ? 'block' : 'none' }}>


            <br/>
                {
                    filterOptions.map((f) => <label key={f[props.filter]}>
                        <input type="checkbox" value={f[props.filter]} onClick={() => getFilterOptions(f[props.filter])}/>
                        {f[props.filter]}
                        <br />
                    </label>
                    )
                }

            </div>
       </Fragment>
    )
}
