import { Fragment, useEffect, useState } from "react"
import "./singleFilter.css"
import axios from "axios";

export const SingleFilter = (props) => {

    const [filterOptions, setfilterOptions] = useState([]);
    const [isFiltersVisible, setIsFiltersVisible] = useState(true);
    const [selectedCategories, setSelectedCategories] = useState([]);

    var data;
    useEffect(()=> {
        (async () => {
            try {
                data = [{Category: "home_tutions"},{Category: "online"},{Category: "in_person"}, {Category: "home_tutions1"}, {Category: "home_tutions2"},{Category: "home_tutions3"}, {Category: "home_tutions4"}]                

                setfilterOptions(data)
            } catch (error) {
                console.log(error)
            }
        })()
    }, [])



    const handleOnClick = async () => {
        setIsFiltersVisible((prev) => !prev);
    }



    const getFilterOptions = (category) => {
        setSelectedCategories(prevCategories => {
            if (prevCategories.includes(category)) {

                return prevCategories.filter(c => c !== category);
            } else {

                return [...prevCategories, category];
            }
        });

    };
    const queryParameters = {
        Category: selectedCategories,

    };
    console.log(queryParameters) 

    const params = new URLSearchParams();
        for (const key in queryParameters) {
            if (Array.isArray(queryParameters[key])) {
                queryParameters[key].forEach(value => params.append(key, value));
            } else {
                params.append(key, queryParameters[key]);
            }
        }

        try {
            const response =  axios.get(`http://localhost:3500/api/filter`, { params });
            console.log(response.data);
        } catch (error) {
            console.error("Error fetching data:", error);
        }


    return (
       <Fragment>
        <button onClick={handleOnClick}>
                <span className="material-icons-outlined" >
                {isFiltersVisible ? "keyboard_arrow_up" : "keyboard_arrow_down"}
                </span>
        </button>
        <h3>{props.filter}</h3>

            <div id="filters" style={{ display: isFiltersVisible ? 'block' : 'none' }}>


            <br/>
                {
                    filterOptions.map((f) => <label>
                        <input type="checkbox" value={f.Category} onClick={() => getFilterOptions(f.Category)}/>
                        {f.Category} 
                        <br />
                    </label>
                    )
                }

            </div>
       </Fragment>
    )
}
