import '../RelatedCourses/RelatedCourses.css'
import { useState, useEffect } from 'react';


export const RelatedCourses = ({singleProfile}) => {

    const [queryParameters, setQueryParameters] = useState({ Class: [], Subject:[] });

    const {Course} = singleProfile
    console.log(Course)

    useEffect(() => {
        if (Course) {
            const classes = [];
            const subjects = [];
            Course.forEach(element => {
                if (!classes.includes(element.Class)) {
                    classes.push(element.Class);
                }
                if (!subjects.includes(element.Subject)) {
                    subjects.push(element.Subject);
                }
            });

            setQueryParameters({ Class: classes, Subject: subjects });
        }
    }, [Course]);

    const params = new URLSearchParams();
    for (const key in queryParameters) {
        if (Array.isArray(queryParameters[key])) {
            queryParameters[key].forEach(value => params.append(key, value));
        } else {
            console.log(key, queryParameters[key]);
            params.append(key, queryParameters[key]);
        }
    }
    return <h3>RelatedCourses</h3>
}
