import { Fragment, useEffect, useState } from "react"
import "./singleFilter.css"

export const SingleFilter = () => {

    const [filterOptions, setfilterOptions] = useState([]);
    const [noOfFilterOptions, setnoOfFilterOptions] = useState(0);
    const [isFiltersVisible, setIsFiltersVisible] = useState(true);

    var data;
    useEffect(()=> {
        (async () => {
            try {
                data = [{Category: "home_tutions"},{Category: "online"},{Category: "in_person"}, {Category: "home_tutions1"}, {Category: "home_tutions2"},{Category: "home_tutions3"}, {Category: "home_tutions4"}]                
                // const filteroptionstoShow = data.slice(noOfFilterOptions,noOfFilterOptions+3);
                // console.log(filteroptionstoShow);
                // setfilterOptions(filteroptionstoShow);
                setfilterOptions(data)
            } catch (error) {
                console.log(error)
            }
        })()
    }, [noOfFilterOptions])

    // const handleScrolldown = () => {
    //     setnoOfFilterOptions((prev) => prev+3);
    // }

    // const handleScrollUp = () => {
    //     setnoOfFilterOptions((prev) => prev-3);
    // }

    const handleOnClick = () => {
        setIsFiltersVisible((prev) => !prev);
    }

    return (
       <Fragment>
        <button onClick={handleOnClick}>
                <span className="material-icons-outlined" >
                {isFiltersVisible ? "keyboard_arrow_up" : "keyboard_arrow_down"}
                </span>
        </button>
        <h3>Filter Tutors</h3>


            <div id="filters" style={{ display: isFiltersVisible ? 'block' : 'none' }}>

                {/* <button onClick={handleScrollUp}>
                <span className="material-icons-outlined" >keyboard_arrow_up</span>
                </button> */}

            <br/>
                {
                    filterOptions.map((f) => <label>
                        <input type="checkbox" value={f.Category}/>
                        {f.Category} 
                        <br />
                    </label>
                    )
                }
                {/* <button onClick={handleScrolldown}>
                <span className="material-icons-outlined">keyboard_arrow_down</span>
                </button> */}
            </div>
       </Fragment>
    )
}
